function parseItemArray($array) {
  if (! isset($array->item) && ! is_array($array)) {
    return null;
  }
  
  $result = array ();
  if (isset($array->item)) {
    foreach ( $array->item as $key => $value ) {
      $result[$value->key] = $value->value;
    }
  } else {
    foreach ( $array as $key => $value ) {
